import React, { useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'wowjs/css/libs/animate.css';
import 'owl.carousel/dist/assets/owl.carousel.css';
import $ from 'jquery'; // Import jQuery for Owl Carousel
import 'owl.carousel';
import WOW from 'wowjs'; // Import WOW.js

const Testimonial = () => {
  useEffect(() => {
    // WOW.js Initialization
    new WOW().init();

    // Owl Carousel Initialization
    $('.testimonial-carousel').owlCarousel({
      autoplay: true,
      smartSpeed: 1000,
      center: true,
      dots: false,
      loop: true,
      nav: true,
      navText: [
        '<i class="bi bi-chevron-left"></i>',
        '<i class="bi bi-chevron-right"></i>'
      ],
      responsive: {
        0: { items: 1 },
        576: { items: 1 },
        768: { items: 2 },
        992: { items: 3 }
      }
    });

    // Client carousel initialization
    $('.client-carousel').owlCarousel({
      autoplay: true,
      smartSpeed: 1000,
      margin: 90,
      dots: false,
      loop: true,
      nav: false,
      responsive: {
        0: { items: 2 },
        576: { items: 3 },
        768: { items: 4 },
        992: { items: 5 },
        1200: { items: 6 }
      }
    });

    // Sticky Navbar
    const handleScroll = () => {
      if (window.scrollY > 45) {
        document.querySelector('.navbar').classList.add('sticky-top', 'shadow-sm');
      } else {
        document.querySelector('.navbar').classList.remove('sticky-top', 'shadow-sm');
      }

      // Back to top button visibility
      if (window.scrollY > 100) {
        document.querySelector('.back-to-top').style.display = 'block';
      } else {
        document.querySelector('.back-to-top').style.display = 'none';
      }
    };

    window.addEventListener('scroll', handleScroll);

    // Back to top functionality
    document.querySelector('.back-to-top').addEventListener('click', () => {
      window.scrollTo({ top: 0, behavior: 'smooth' });
    });

    // Cleanup event listener on component unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="container-xxl bg-white p-0">
      <div className="container-xxl py-6">
        <div className="container">
          <div className="mx-auto text-center wow fadeInUp" data-wow-delay="0.1s" style={{ maxWidth: '600px' }}>
            <div className="d-inline-block border rounded-pill text-primary px-4 mb-3">Testimonial</div>
            <h2 className="mb-5">What Our Clients Say!</h2>
          </div>
          <div className="owl-carousel testimonial-carousel wow fadeInUp" data-wow-delay="0.1s">
            <div className="testimonial-item rounded p-4">
              <i className="fa fa-quote-left fa-2x text-primary mb-3"></i>
              <p>Dolor et eos labore, stet justo sed est sed. Diam sed sed dolor stet amet eirmod eos labore diam</p>
              <div className="d-flex align-items-center">
                <img className="img-fluid flex-shrink-0 rounded-circle" src="img/testimonial-1.jpg" alt="Client 1" />
                <div className="ps-3">
                  <h6 className="mb-1">Client Name</h6>
                  <small>Profession</small>
                </div>
              </div>
            </div>
            <div className="testimonial-item rounded p-4">
              <i className="fa fa-quote-left fa-2x text-primary mb-3"></i>
              <p>Dolor et eos labore, stet justo sed est sed. Diam sed sed dolor stet amet eirmod eos labore diam</p>
              <div className="d-flex align-items-center">
                <img className="img-fluid flex-shrink-0 rounded-circle" src="img/testimonial-2.jpg" alt="Client 2" />
                <div className="ps-3">
                  <h6 className="mb-1">Client Name</h6>
                  <small>Profession</small>
                </div>
              </div>
            </div>
            <div className="testimonial-item rounded p-4">
              <i className="fa fa-quote-left fa-2x text-primary mb-3"></i>
              <p>Dolor et eos labore, stet justo sed est sed. Diam sed sed dolor stet amet eirmod eos labore diam</p>
              <div className="d-flex align-items-center">
                <img className="img-fluid flex-shrink-0 rounded-circle" src="img/testimonial-3.jpg" alt="Client 3" />
                <div className="ps-3">
                  <h6 className="mb-1">Client Name</h6>
                  <small>Profession</small>
                </div>
              </div>
            </div>
            <div className="testimonial-item rounded p-4">
              <i className="fa fa-quote-left fa-2x text-primary mb-3"></i>
              <p>Dolor et eos labore, stet justo sed est sed. Diam sed sed dolor stet amet eirmod eos labore diam</p>
              <div className="d-flex align-items-center">
                <img className="img-fluid flex-shrink-0 rounded-circle" src="img/testimonial-4.jpg" alt="Client 4" />
                <div className="ps-3">
                  <h6 className="mb-1">Client Name</h6>
                  <small>Profession</small>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Back to Top Button */}
      <a href="#" className="btn btn-lg btn-primary btn-lg-square back-to-top"><i className="bi bi-arrow-up"></i></a>
    </div>
  );
};

export default Testimonial;
