import React, { useEffect } from 'react';
import WOW from 'wowjs';
import "style.css"
import 'animate.css';

const Hero = ({heading, page, link}) => {
  useEffect(() => {
    // Initialize WOW.js
    new WOW.WOW({
      boxClass: 'wow',        // default
      animateClass: 'animated', // default
      offset: 0,              // Adjust this to trigger animations earlier or later
      mobile: true,           // Enable animations on mobile
      live: true              // Act on asynchronously loaded content
    }).init();
  }, []);

  return (
    <div className="container-fluid bg-primary page-header">
      <div className="container text-center">
        <h1 className="text-white animated animate__zoomIn mb-3">{heading}</h1>
        <nav aria-label="breadcrumb">
          <ol className="breadcrumb justify-content-center">
            <li className="breadcrumb-item"><a className="text-white" href="#">Home</a></li>
            <li className="breadcrumb-item"><a className="text-white" href="#">Pages</a></li>
            <li className="breadcrumb-item"><a className="text-white" href={link}>{page}</a></li>
          </ol>
        </nav>
      </div>
    </div>
  );
}

export default Hero;
