import React, { useEffect } from 'react';
import WOW from 'wowjs';
import "style.css";
import 'animate.css'; 

const AboutSection = () => {
    useEffect(() => {
      const wow = new WOW.WOW({
        live: true, // Only check the DOM once
        mobile: false, // Disable animations on mobile devices
      });

      wow.init(); // Initialize WOW.js

      // Explicitly remove WOW.js animations on mobile
      const disableAnimations = () => {
        document.querySelectorAll('.wow').forEach(el => {
          el.style.animation = 'none'; // Disable animation
        });
      };

      if (window.innerWidth <= 768) {
        disableAnimations(); // Disable animations if on mobile
      }

      window.addEventListener('resize', () => {
        if (window.innerWidth <= 768) {
          disableAnimations();
        }
      });

      // Clean up WOW.js synchronization
      return () => {
        wow.sync();
        window.removeEventListener('resize', disableAnimations);
      };
    }, []);
  
