import React from 'react';

// Define the data for services
const servicesData = [
  {
    title: "Targeted Recruitment",
    description: "We leverage our extensive network and proven recruitment strategies to identify top-tier talent with the specific skills and experience your startup needs. We focus on finding candidates who are not only technically proficient but also possess excellent communication skills, a strong work ethic, and the ability to thrive in a remote work environment.",
  },
  {
    title: "Specialized Training",
    description: "We go beyond simply placing candidates. We provide customized training programs designed to equip your new hires with the product knowledge, technical expertise, and customer service skills they need to excel in their roles. This ensures they can provide effective solutions and exceptional support from day one.",
  },
  {
    title: "Focus on Sales Roles",
    description: "We focus on providing talent for sales roles like Inside Sales Representatives, Sales Development Representatives (SDRs), and Account Managers. We recruit and train skilled professionals who can generate leads, qualify prospects, close deals, and build strong client relationships.",
    subServices: [
      {
        role: "Inside Sales Representatives",
        description: "We recruit and train skilled inside sales representatives who can generate leads, qualify prospects, and close deals remotely."
      },
      {
        role: "Sales Development Representatives (SDRs)",
        description: "We find driven SDRs who can effectively prospect and qualify leads to fuel your sales pipeline."
      },
      {
        role: "Account Managers",
        description: "We source experienced account managers who can build strong relationships with existing clients and drive customer retention."
      }
    ]
  },
  {
    title: "Focus on Technical Support Roles",
    description: "We focus on providing talent for technical support roles such as Technical Support Specialists (Tier 1, Tier 2) and Customer Support Representatives. These professionals can provide timely and effective solutions to customer issues.",
    subServices: [
      {
        role: "Technical Support Specialists (Tier 1, Tier 2)",
        description: "We recruit and train technical support specialists who can provide timely and effective solutions to customer issues."
      },
      {
        role: "Customer Support Representatives",
        description: "We find empathetic and resourceful representatives who can address customer inquiries and ensure a positive customer experience."
      }
    ]
  },
  {
    title: "Remote-Ready Professionals",
    description: "We prioritize candidates with proven remote work experience and the necessary tools and resources to work effectively from home."
  },
  {
    title: "Ongoing Support",
    description: "We provide ongoing support and communication to both our clients and our placed candidates to ensure a smooth onboarding process and long-term success."
  }
];

const Service = () => {
  return (
    <div className="container-xxl py-6">
      <div className="container">
        <div className="mx-auto text-center" style={{ maxWidth: '600px' }}>
          <div className="d-inline-block border rounded-pill text-primary px-4 mb-3">What We Offer</div>
          <h2 className="mb-5">Our Services</h2>
        </div>
        <div className="row g-4">
          {servicesData.map((service, index) => (
            <div className="col-lg-4 col-md-6" key={index}>
              <div className="service-item rounded h-100">
                <div className="p-5">
                  <h5 className="mb-3">{service.title}</h5>
                  <p>{service.description}</p>
                  {service.subServices && (
                    <div className="mt-4">
                      <h6>Roles:</h6>
                      <ul>
                        {service.subServices.map((subService, subIndex) => (
                          <li key={subIndex}>
                            <strong>{subService.role}:</strong> {subService.description}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Service;
