import React, { useEffect, useState } from 'react';
import WOW from 'wowjs';
import About from './About';
import Service from './Service';
import Features from './Features';
import Team from "./Team"
import Testimonial from './Testimonial';
import "style.css"
import 'animate.css';

const Home = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);

    // Initialize WOW.js only for larger screens
    if (!isMobile) {
      new WOW.WOW({
        live: false, // For performance, only check DOM once
      }).init();
    }

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [isMobile]);

    return (
        <div>
          <div className="container-fluid bg-primary hero-header">
            <div className="container">
                <div className="row g-5 align-items-center">
                    <div className="col-lg-6 text-center text-lg-start">
                        <h1 className="text-white mb-4 animated animate__zoomIn">Unlock your potential with personalized consultation services</h1>
                        <p className="text-white pb-3 animated animate__zoomIn">
                            A consultation service that emphasizes its ability to provide tailored solutions to each client's unique needs. The website could highlight the personalized approach and the extensive range of services offered, from business strategy and marketing to HR and finance.
                        </p>
                        <a href="/" className="btn btn-outline-light rounded-pill border-2 py-3 px-5 animated animate__slideInRight">Learn More</a>
                    </div>
                    <div className="col-lg-6 text-center text-lg-start">
                        <img className="img-fluid animated animate__zoomIn" src="img/hero.png" alt="Hero Image" />
                    </div>
                </div>
            </div>
          </div>
          <About/>
          <Service/>
          <Features/>
          <Team/>
          <Testimonial/>
        </div>
        
    );
};

export default Home;
