import React, { useEffect, useState } from 'react';
import WOW from 'wowjs';
import "style.css";
import 'animate.css';
import ServiceItem from './ServiceItem'; // Import ServiceItem Component

const Service = () => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);

    // Initialize WOW.js only for larger screens
    if (!isMobile) {
      new WOW.WOW({
        live: false, // For performance, only check DOM once
      }).init();
    }

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [isMobile]);

  const services = [
    {
      icon: 'fa-user-tie',
      title: 'Business Research',
      description: 'Erat ipsum justo amet duo et elitr dolor, est duo duo eos lorem sed diam stet diam sed stet lorem.',
      delay: '0.1s',
    },
    {
      icon: 'fa-chart-pie',
      title: 'Strategic Planning',
      description: 'Erat ipsum justo amet duo et elitr dolor, est duo duo eos lorem sed diam stet diam sed stet lorem.',
      delay: '0.3s',
    },
    {
      icon: 'fa-chart-line',
      title: 'Market Analysis',
      description: 'Erat ipsum justo amet duo et elitr dolor, est duo duo eos lorem sed diam stet diam sed stet lorem.',
      delay: '0.6s',
    },
    {
      icon: 'fa-chart-area',
      title: 'Financial Analysis',
      description: 'Erat ipsum justo amet duo et elitr dolor, est duo duo eos lorem sed diam stet diam sed stet lorem.',
      delay: '0.1s',
    },
    {
      icon: 'fa-balance-scale',
      title: 'Legal Advisory',
      description: 'Erat ipsum justo amet duo et elitr dolor, est duo duo eos lorem sed diam stet diam sed stet lorem.',
      delay: '0.3s',
    },
    {
      icon: 'fa-house-damage',
      title: 'Tax & Insurance',
      description: 'Erat ipsum justo amet duo et elitr dolor, est duo duo eos lorem sed diam stet diam sed stet lorem.',
      delay: '0.6s',
    },
  ];

  return (
    <div className="container-xxl py-6">
      <div className="container">
        <div className="mx-auto text-center wow animate__slideInUp" data-wow-delay="0.1s" style={{ maxWidth: '600px' }}>
          <div className="d-inline-block border rounded-pill text-primary px-4 mb-3">Our Services</div>
          <h2 className="mb-5">We Provide Solutions On Your Business</h2>
        </div>
        <div className="row g-4">
          {services.map((service, index) => (
            <ServiceItem
              key={index}
              icon={service.icon}
              title={service.title}
              description={service.description}
              delay={service.delay}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Service;
