import React, { useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'wowjs/css/libs/animate.css';
import 'owl.carousel/dist/assets/owl.carousel.css'; // Import Owl Carousel CSS
import WOW from 'wowjs'; // Import WOW.js
import $ from 'jquery'; // Import jQuery
import 'owl.carousel'; // Import Owl Carousel JS

const Testimonial = () => {
  useEffect(() => {
    // Initialize WOW.js
    new WOW.WOW().init();

    // Ensure jQuery is loaded before initializing Owl Carousel
    if ($) {
      $('.testimonial-carousel').owlCarousel({
        autoplay: true,
        smartSpeed: 1000,
        center: true,
        dots: false,
        loop: true,
        nav: true,
        navText: [
          '<i class="bi bi-chevron-left"></i>',
          '<i class="bi bi-chevron-right"></i>',
        ],
        responsive: {
          0: { items: 1 },
          576: { items: 1 },
          768: { items: 2 },
          992: { items: 3 },
        },
      });
    } else {
      console.error('jQuery is not available.');
    }

    // Sticky Navbar and Back-to-Top Button
    const handleScroll = () => {
      const navbar = document.querySelector('.navbar');
      const backToTop = document.querySelector('.back-to-top');

      if (navbar) {
        if (window.scrollY > 45) {
          navbar.classList.add('sticky-top', 'shadow-sm');
        } else {
          navbar.classList.remove('sticky-top', 'shadow-sm');
        }
      }

      if (backToTop) {
        if (window.scrollY > 100) {
          backToTop.style.display = 'block';
        } else {
          backToTop.style.display = 'none';
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    const backToTopButton = document.querySelector('.back-to-top');
    if (backToTopButton) {
      backToTopButton.addEventListener('click', () => {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      });
    }

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Testimonials Data
  const testimonials = [
    {
      text: 'Dolor et eos labore, stet justo sed est sed. Diam sed sed dolor stet amet eirmod eos labore diam.',
      imgSrc: '/img/testimonial-1.jpg',
      name: 'Client Name 1',
      profession: 'Profession 1',
    },
    {
      text: 'Dolor et eos labore, stet justo sed est sed. Diam sed sed dolor stet amet eirmod eos labore diam.',
      imgSrc: '/img/testimonial-2.jpg',
      name: 'Client Name 2',
      profession: 'Profession 2',
    },
    // Add more testimonials as needed
  ];

  return (
    <div className="container-xxl bg-white p-0">
      <div className="container-xxl py-6">
        <div className="container">
          <div
            className="mx-auto text-center wow fadeInUp"
            data-wow-delay="0.1s"
            style={{ maxWidth: '600px' }}
          >
            <div className="d-inline-block border rounded-pill text-primary px-4 mb-3">Testimonial</div>
            <h2 className="mb-5">What Our Clients Say!</h2>
          </div>
          <div className="owl-carousel testimonial-carousel wow fadeInUp" data-wow-delay="0.1s">
            {testimonials.map((testimonial, index) => (
              <div className="testimonial-item rounded p-4" key={index}>
                <i className="fa fa-quote-left fa-2x text-primary mb-3"></i>
                <p>{testimonial.text}</p>
                <div className="d-flex align-items-center">
                  <img
                    className="img-fluid flex-shrink-0 rounded-circle"
                    src={testimonial.imgSrc}
                    alt={`Client ${index + 1}`}
                  />
                  <div className="ps-3">
                    <h6 className="mb-1">{testimonial.name}</h6>
                    <small>{testimonial.profession}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Back to Top Button */}
      <a href="#" className="btn btn-lg btn-primary btn-lg-square back-to-top">
        <i className="bi bi-arrow-up"></i>
      </a>
    </div>
  );
};

export default Testimonial;
