// Services.js
import React from 'react';
import ServiceItem from './ServiceItem';

const services = [
  {
    icon: "fa-user-tie",
    title: "Customer Support",
    description: "We provide empathetic and resourceful customer support representatives who can address inquiries and ensure a positive customer experience.",
  },
  {
    icon: "fa-tools",
    title: "Technical Support",
    description: "Our technical support specialists are trained to provide timely and effective solutions to customer issues, ensuring smooth operations.",
  },
  {
    icon: "fa-chart-line",
    title: "Sales Support",
    description: "We recruit and train skilled sales representatives who can generate leads, qualify prospects, and close deals remotely.",
  },
];

const Services = () => (
  <div>
  <header className="text-center mb-5 ml-5">
     <h1>Zenith Solutions - Your Partner in Growth</h1>
        <p>
          Is your tech startup ready to scale but struggling to find the right support talent? 
          Zenith Solutions specializes in connecting innovative startups like yours with highly skilled remote professionals from India. 
          We provide comprehensive solutions that include Customer Support, Technical Support, and Sales Support to ensure your success.
        </p>
  </header>
 
  <section className="services mb-5">
    <h2>Our Services</h2>
    <div className="row g-4">
      {services.map((service, index) => (
        <ServiceItem
          key={index}
          icon={service.icon}
          title={service.title}
          description={service.description}
        />
      ))}
    </div>
  </section>
  <section className="benefits mb-5">
  <h2>Benefits of Choosing Zenith Solutions</h2>
  <ul>
      <li><strong>Reduced Hiring Costs:</strong> Save on recruitment fees, office space, and other overhead costs associated with traditional hiring.</li>
      <li><strong>Access to a Global Talent Pool:</strong> Tap into the vast pool of skilled professionals in India.</li>
      <li><strong>Faster Time to Hire:</strong> Quickly scale your team with our efficient recruitment process.</li>
      <li><strong>Increased Productivity:</strong> Benefit from dedicated and trained remote professionals who are focused on delivering results.</li>
      <li><strong>Focus on Your Core Business:</strong> Delegate your recruitment and training needs to us so you can focus on growing your startup.</li>
  </ul>
</section>
</div>
);

export default Services;
