import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Ensure Bootstrap CSS is imported
import 'style.css'; // Import existing CSS
import 'animate.css'; // Import animation CSS

const servicesData = [
    {
        title: "Targeted Recruitment",
        description: "We leverage our extensive network and proven recruitment strategies to identify top-tier talent with the specific skills and experience your startup needs.",
        icon: "bi-person-circle", // Using Bootstrap Icons
        imgSrc: "/img/inside-sales-logo.png" // Image path for the service
    },
    {
        title: "Specialized Training",
        description: "We provide customized training programs designed to equip your new hires with the product knowledge, technical expertise, and customer service skills they need to excel in their roles.",
        icon: "bi-book",
        imgSrc: "/img/sdr-logo.png"
    },
    {
        title: "Focus on Sales Roles",
        description: "We recruit and train skilled inside sales representatives who can generate leads, qualify prospects, and close deals remotely.",
        icon: "bi-graph-up-arrow",
        imgSrc: "/img/account-manager-logo.png"
    },
    {
        title: "Focus on Technical Support Roles",
        description: "We recruit and train technical support specialists who can provide timely and effective solutions to customer issues.",
        icon: "bi-gear",
        imgSrc: "/img/technical-support-logo.png"
    },
    {
        title: "Remote-Ready Professionals",
        description: "We prioritize candidates with proven remote work experience and the necessary tools and resources to work effectively from home.",
        icon: "bi-laptop",
        imgSrc: "/img/customer-support-logo.png"
    },
    {
        title: "Ongoing Support",
        description: "We provide ongoing support and communication to both our clients and our placed candidates to ensure a smooth onboarding process and long-term success.",
        icon: "bi-headset",
        imgSrc: "/img/ongoing-support-logo.png"
    }
];

const Service = () => {
    return (
        <div className="container-xxl py-6">
            <div className="container">
                <div className="mx-auto text-center" style={{ maxWidth: '600px' }}>
                    <div className="d-inline-block border rounded-pill text-primary px-4 mb-3">What We Offer</div>
                    <h2 className="mb-5">Our Services</h2>
                </div>
                <div className="row g-4">
                    {servicesData.map((service, index) => (
                        <div className="col-lg-4 col-md-6" key={index}>
                            <div className="service-item rounded h-100 shadow-sm">
                                <div className="p-5 text-center">
                                    <img src={service.imgSrc} alt={service.title} className="mb-4" style={{ width: '60px', height: '60px' }} />
                                    <h5 className="mb-3">{service.title}</h5>
                                    <p>{service.description}</p>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Service;
